import SwiftUI

/// A Voice Tuner–style slider row used across VoiceKitUI.
/// Left column: icon, title, and live value (primary color).
/// Right: a SwiftUI Slider with the standard blue accent.
public struct TunerSliderRow: View {
    public var title: String
    public var systemImage: String
    @Binding public var value: Double
    public var range: ClosedRange<Double>
    public var step: Double
    public var formatted: (Double) -> String

    public init(
        title: String,
        systemImage: String,
        value: Binding<Double>,
        range: ClosedRange<Double>,
        step: Double = 0.01,
        formatted: @escaping (Double) -> String = { String(format: "%.2f", $0) }
    ) {
        self.title = title
        self.systemImage = systemImage
        self._value = value
        self.range = range
        self.step = step
        self.formatted = formatted
    }

    public var body: some View {
        HStack(spacing: 10) {
            VStack(spacing: 2) {
                Image(systemName: systemImage)
                    .imageScale(.medium)
                    .font(.footnote)
                    .fontWeight(.semibold)
                    .foregroundStyle(.primary)
                Text(title)
                    .font(.footnote)
                    .foregroundStyle(.primary)
                Text(formatted(value))
                    .font(.footnote)
                    .monospacedDigit()
                    .foregroundStyle(.primary)
            }
            .frame(width: 60)

            VStack(spacing: 6) {
                Slider(value: $value, in: range, step: step)
                    .tint(.blue)
            }
        }
        .frame(maxWidth: .infinity, alignment: .leading)
    }
}

