import XCTest
import VoiceKit

final class VoiceOpGateForceClearTests: XCTestCase {

    func testForceClearAllowsImmediateAcquire() async {
        let gate = VoiceOpGate()
        await gate.acquire()
        await gate.forceClear() // forget lock without waiting
        // Should not block
        await gate.acquire()
        await gate.release()
        XCTAssertTrue(true)
    }
}
